import s from './Dialogs.module.css';
import { NavLink } from 'react-router-dom';

const DialogItem = ({ name }) => {
    return (
        <div>
                <NavLink to="sveta" className={({isActive}) => `${s.dialog} ${(isActive ? `${s.active}` : "")}`}>
                    { name }
                </NavLink>
        </div>
    )
}

const Message = ({ message }) => {
    return (
        <div className={s.message}>{ message }</div>
    )
}

const Dialogs = () => {
    return (
        <div className={s.dialogs}>
            <div className={s.dialogsItem}>
                <DialogItem name="Sveta"/>   
                <DialogItem name="Pasha"/>  
                <DialogItem name="Olya"/>  
                <DialogItem name="Serge"/>  
                <DialogItem name="Kolya"/>   
            </div>
            <div className={s.messages}>
                <Message message="Hi!"/>
                <Message message="How are you?"/>
                <Message message="How is your course?"/>
            </div>
        </div >
    )
}

export default Dialogs;
