import axios from "axios";

// экземпляр axios. В нем все настройки запросов
const instance = axios.create({
    withCredentials: true,
    baseURL: 'https://social-network.samuraijs.com/api/1.0/',
    header: {
        "API-KEY": ""
    }
})

export const getUsers = (currentPage=1, pageSize=10) => {
    return instance.axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${currentPage}&count=${pageSize}`)
        .then(response => {
            return response.data; // чтобы в компоненту не передавалось ничего лишнего
        })
}


