const UPDATE_NEW_MESSAGE_BODY = "UPDATE_NEW_MESSAGE_BODY";
const SEND_MESSAGE = 'SEND_MESSAGE';

let initialState = {
    dialogs: [
      { id: 1, name: "Sveta" }, // dialog
      { id: 2, name: "Pasha" }, // dialog
      { id: 3, name: "Olya" }, // dialog
      { id: 4, name: "Serge" }, // dialog
      { id: 5, name: "Kolya" }, // dialog
    ],
    messages: [
      { id: 1, message: "Hi!" }, // message
      { id: 2, message: "How are you?" }, // message
      { id: 3, message: "How is your course?" }, // message
      { id: 4, message: "Yo" }, // message
      { id: 5, message: "Yo" }, // message
    ],
    newMessagesBody: ""
  };

const dialogsReducer = (state = initialState, action) => {

    switch (action.type) {
        case UPDATE_NEW_MESSAGE_BODY:
            state.newMessagesBody = action.body; // state = this._state.dialogsPage
            return state;
        case SEND_MESSAGE:
            let body = state.newMessagesBody; // state=this._state.dialogsPage
            state.newMessagesBody = ''; // state=this._state.dialogsPage
            state.messages.push({ id: 6, message: body }); // state=this._state.dialogsPage

            return state;
        default:
            return state;
    }

}

    export default dialogsReducer;
