import React from "react";
import styles from './users.module.css';
import axios from 'axios';
// экспортируется много всего, а мы это упаковываем в один объект axios, а мы будем обращаться через этот объект ко всему тому, что импортировали:
// import * as axios from "axios";


const Users = (props) => {

    if (props.users.length === 0) {
            axios.get("https://social-network.samuraijs.com/api/1.0/users")
            .then(response => {
                // debugger
                props.setUsers(response.data.items);
                console.log(response)
            });
    }

    return <div>
        {
            props.users.map(user => <div key={user.id}>
                <span>
                    <div>
                        <img src={ user.photos.small != null ? user.photos.small : "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR0Sdnql1woODobQltguzJsGz8BnHKJlm6hJw&usqp=CAU"} className={styles.userPhoto} />
                    </div>
                    <div>
                        {user.followed
                            ? <button onClick={() => { props.unfollow(user.id) }} >Unfollow</button>
                            : <button onClick={() => { props.follow(user.id) }} >Follow</button>}

                    </div>
                </span>
                <span>
                    <span>
                        <div>{user.name}</div>
                        <div>{user.status}</div>
                    </span>
                    <span>
                        <div>{"user.location.country"}</div>
                        <div>{"user.location.city"}</div>
                    </span>
                </span>
            </div>)
        }

    </div>
}


export default Users;
